[{"id":"632d581b-9cf4-46ff-862b-094d8095cd9d","title":"menu card","install":"npm install gsap","usage":"import StaggeredMenu from './StaggeredMenu';

const menuItems = [
  { label: 'Home', ariaLabel: 'Go to home page', link: '/' },
  { label: 'About', ariaLabel: 'Learn about us', link: '/about' },
